import ExerciseList from './ExerciseList';
import { useEffect, useState } from 'react';
import { searchExercises } from '../../api/api';
import useFetch from '../../hooks/useFetch';
import { useUserContext } from '../../hooks/UserContext';
import { useNavigate } from 'react-router-dom';
import styles from './Exercise.module.css';
import { faCircleXmark } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default function Exercises() {
    const { user } = useUserContext();
    const { 
        loading, 
        data: exercises, 
        error, 
        setData: setExercises, 
        setLoading, 
        setError 
    } = useFetch(user && `/users/${user?.id}/exercises`);
    const [searchText, setSearchText] = useState('');
    const navigate = useNavigate();

    const handleChange = e => {
        setSearchText(e.target.value);
    };

    const clearSearch = e => {
        e.target.parentElement.firstChild.value = "";
        setSearchText("");
    };

    useEffect(() => {
        if (!user) navigate('/login');
        if (exercises?.authorisationFailed) {
            navigate('/logout');
        }
    }, [exercises, user]);

    useEffect(() => {
        if (searchText) {
            setLoading(true);
            const controller = new AbortController();
            searchExercises(user.id, searchText, controller)
                .then(res => {
                    if (res?.message === 'You must be logged in as this user to access this resource') navigate('/logout');
                    else if (res) {
                        setExercises(res);
                        setError(false);
                    } else setError(true);
                })
                .catch(e => {
                    setError(true);
                })
                .finally(() => setLoading(false));
            return () => {
                controller.abort();
            }
        } else {
            setLoading(true);
            const controller = new AbortController();
            searchExercises(user.id, controller)
                .then(res => {
                    if (res?.message === 'You must be logged in as this user to access this resource') navigate('/logout');
                    else if (res) {
                        setExercises(res);
                        setError(false);
                    } else setError(true);
                })
                .catch(e => {
                    setError(true);
                })
                .finally(() => setLoading(false));
            return () => {
                controller.abort();
            }
        }
    }, [searchText]);

    return (
        <>
            <h1>Exercises</h1>

            {
                loading ? <p>Loading...</p> : error ? <p>error</p> : undefined
            }

            <div className={styles.search}>
                <input className={styles.input} type="text" onChange={handleChange} placeholder="Search" />
                <button className={!searchText ? "hidden" : undefined} onClick={clearSearch}>
                    <FontAwesomeIcon icon={faCircleXmark} />
                </button>
            </div>
            
            {exercises &&
                <ExerciseList exercises={exercises.exercises} />
            }
        </>
    );
}
