import { NavLink, Link } from 'react-router-dom';
import { useUserContext } from '../../hooks/UserContext';
import styles from './Header.module.css';
import { faPlus, faRightToBracket, faRightFromBracket, faUser } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default function Header() {
    const { user } = useUserContext();

    return (
        <header className={styles.header}>
            <div className={styles['header-content']}>
                <Link className={styles.logo} to="/">Workout Tracker</Link>

                <nav className={styles.nav}>
                    <ul className={styles['nav-list']}>
                        <li key="home">
                            <NavLink 
                                    to="/" 
                                    className={({isActive}) => 
                                        isActive ? `${styles['nav-link']} ${styles['nav-link-active']}` : styles['nav-link']
                                    }
                                >
                                    <FontAwesomeIcon icon={faPlus} />
                                    Start Workout
                            </NavLink>
                        </li>

                        {user && (
                            <li key="profile">
                                <NavLink 
                                        to="/profile" 
                                        className={({isActive}) => 
                                            isActive ? `${styles['nav-link']} ${styles['nav-link-active']}` : styles['nav-link']
                                        }
                                    >
                                        <FontAwesomeIcon icon={faUser} />
                                        Profile
                                </NavLink>
                            </li>
                        )}

                        {!user && (
                            <li key="login">
                                <NavLink 
                                        to="/login" 
                                        className={({isActive}) => 
                                            isActive ? `${styles['nav-link']} ${styles['nav-link-active']}` : styles['nav-link']
                                        }
                                    >
                                        <FontAwesomeIcon icon={faRightToBracket} />
                                        Login
                                </NavLink>
                            </li>
                        )}

                        {user && (
                            <li key="logout">
                                <NavLink 
                                        to="/logout" 
                                        className={({isActive}) => 
                                            isActive ? `${styles['nav-link']} ${styles['nav-link-active']}` : styles['nav-link']
                                        }
                                    >
                                        <FontAwesomeIcon icon={faRightFromBracket} />
                                        Logout
                                </NavLink>
                            </li>
                        )}
                    </ul>

                </nav>
            </div>
        </header>
    );
}
