import { useState } from 'react';
import { loginUser, fetchUser } from '../../api/api';
import { useNavigate } from 'react-router-dom';
import { useUserContext } from '../../hooks/UserContext';

export default function LoginForm() {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const { setUser } = useUserContext();

    const handleChange = e => {
        if (e.target.name === 'email')
            setFormData(prev => ({...prev, email: e.target.value}));
        else if (e.target.name === 'password')
            setFormData(prev => ({...prev, password: e.target.value}));
    };

    const handleSubmit = async e => {
        e.preventDefault();
        setLoading(true);
        const userIdResponse = await loginUser(formData.email, formData.password);

        if (userIdResponse?.message === 'wrong password') {
            setError('Email or password is incorrect.');
        } else if (userIdResponse) {
            const userResponse = await fetchUser(userIdResponse.id);

            if (userResponse?.message === 'user not authorised') {
                setError('Error authorising user');
            } else if (userResponse) {
                setUser({id: userResponse.id, name: userResponse.name});
                navigate('/');
            } else {
                setError('Server error - login failed.');
            }

        } else {
            setError('Server error - login failed.');
        }

        setLoading(false);
    };

    return (
        <form className="form" onSubmit={handleSubmit}>
            <input 
                type="email" 
                name="email"
                onChange={handleChange} 
                placeholder="Enter Email" 
                required 
            />
            <input 
                type="password" 
                name="password" 
                onChange={handleChange} 
                placeholder="Enter Password" 
                required 
            />
            <button type="submit" disabled={loading}>{!loading ? 'Login' : 'Logging in...'}</button>
            <p className={error ? 'error' : 'hidden'}>{error}</p>
        </form>
    );
}
