import Exercise from './Exercise';
import { useEffect, useState } from 'react';
import { searchExercises } from '../../api/api';
import useFetch from '../../hooks/useFetch';
import { useUserContext } from '../../hooks/UserContext';
import { useNavigate } from 'react-router-dom';

export default function Exercises() {
    const { user } = useUserContext();
    const { 
        loading, 
        data: exercises, 
        error, 
        setData: setExercises, 
        setLoading, 
        setError 
    } = useFetch(user && `/users/${user?.id}/exercises`);
    const [searchText, setSearchText] = useState('');
    const navigate = useNavigate();

    const handleChange = e => {
        setSearchText(e.target.value);
    };

    useEffect(() => {
        console.log(exercises);
        if (!user) navigate('/login');
        if (exercises?.authorisationFailed) {
            navigate('/logout');
        }

        if (searchText) {
            setLoading(true);
            const controller = new AbortController();
            searchExercises(user.id, searchText, controller)
                .then(res => {
                    if (res?.message === 'You must be logged in as this user to access this resource') navigate('/logout');
                    else if (res) {
                        setExercises(res);
                        setError(false);
                        console.log(exercises);
                    } else setError(true);
                })
                .catch(e => {
                    setError(true);
                })
                .finally(() => setLoading(false));
            return () => {
                controller.abort();
            }
        }

    }, [searchText, exercises]);

    return (
        <>
            <h1>Exercises</h1>
            <input type="text" onChange={handleChange} />
            
        </>
    );
}
